<style>
    :global(body) {
        background-image: url("/src/assets/index/bg.png");
        background-repeat: no-repeat;
        background-attachment: fixed;
        background-size: cover;
        color: black;
    }
    main {
        opacity: 0;
        animation: fadein 1s linear 0.5s 1 normal forwards;
    }
    @keyframes fadein {
        from {
            opacity: 0;
        }
        to {
            opacity: 1;
        }
    }
    #s1 {
        height: 100vh;
    }
    #s1 > div {
        padding-top: calc(50vh - calc(2 * 96px));
        padding-left: 32px;
    }
    #s1 > div > h1 {
        font-size: 128px;
        font-weight: 500;
    }
    #s1 > div > h2 {
        font-size: 64px;
        width: 75vw;
    }
    #s2 {
        position: relative;
        isolation: isolate;
        overflow: hidden;
    }
    #s2 {
        padding-top: 10vh;
        padding-bottom: 10vh;
        display: flex;
        place-items: center;
        justify-content: space-around;
    }
    #s2 > div {
        border: 1px solid var(--background);
        background-color: white;
        border-radius: 25px;
        padding: 20px;
        filter: drop-shadow(0 10px 10px rgb(0 0 0 / 0.4));
        display: flex;
        place-items: center;
        justify-content: center;
        flex-direction: column;
    }
    #s2 > div > img, #s2 > div > svg {
        width: calc(20vw - 120px);
    }
    #s2 > div > span {
        font-weight: 700;
        font-size: 54px;
    }
    #s2 > div > p {
        font-size: 18px;
    }
    #s3 {
        position: relative;
    }
    /* #s3::before {
        content: '';
        z-index: -1;
        position: absolute;
        inset: 0;
        background-color: #fff;
        opacity: 0.65;
    } */
</style>

<script>
    import { onMount } from 'svelte';

    onMount(() => {
        const activate = () => {
            if ((document.body.scrollTop || document.documentElement.scrollTop) > document.documentElement.clientHeight - 100)
                document.getElementById("s2").classList.add('active');
            else
                document.getElementById("s2").classList.remove('active');
        };
        document.addEventListener('scroll', activate);
        activate();
    });
</script>